import React, { Component } from "react";
import NavbarIn from "./navbar/NavbarIn";
import {Container, Row, Col, ButtonToolbar, ToggleButton, ToggleButtonGroup} from "react-bootstrap";

class Buy extends Component{
    constructor(props) {
        super(); 
        this.state = {

        };
      }

      changeRoute = (name) =>{
        this.props.changeRoute(name); 
      }

      unloadUser = () => {
        this.props.unloadUser();
      } 

      render(){
          return(
              <div>
                  <NavbarIn changeRoute={this.changeRoute} unloadUser={this.unloadUser}/>
                  <br/><br/><br/>
                  <div className="tc f3 solidBR">
                  <Container>
          <Row>
            <Col>
              How many Bs would you like to buy?
              <ButtonToolbar className="center mw5 mw7-ns center bg-light-gray pa3 ph5-ns">
                <ToggleButtonGroup
                  className="center "
                  type="radio"
                  name="options"
                  defaultValue={1}
                >

                  <ToggleButton className="ma3" value={1}>
                    B100 for R50
                  </ToggleButton>
                  <ToggleButton className="ma3" value={2}>
                    B200 for R100
                  </ToggleButton>
                  </ToggleButtonGroup>
                  </ButtonToolbar>
                  </Col>
                  </Row>
                  <Row>
                      <Col>
                      <ButtonToolbar className="center mw5 mw7-ns center bg-light-gray pa3 ph5-ns">
                      <ToggleButtonGroup
                  className="center "
                  type="radio"
                  name="options"
                  defaultValue={1}
                >
                  <ToggleButton className="ma3" value={3}>
                    B500 for R250
                  </ToggleButton>
                  <ToggleButton className="ma3" value={4}>
                    B1000 for R500
                  </ToggleButton>
                  </ToggleButtonGroup>
              </ButtonToolbar>
              </Col>
              </Row>
              </Container>
              </div>
              </div>
          )
      }
}

export default Buy;
