import React, { Component } from "react";
import {Modal, Col, Row, Container, Button} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import "./bets.css";
import MyVerticallyCenteredModal from './MyVerticallyCenteredModal';

let mods;
let show;
 


let bHolds;
class PileIndiBet extends Component{
constructor(props) {
    super();
    this.state = {
      id: 111111, 
      Price: 0,
      Odds: "",
      Expiry:"",
      BetDiscription:"XXX",
      modalShow: false,

    };
  }
  modalClose = () => {
    mods = <div></div>;      

      this.setState({ modalShow: false });
    }
handleShow = () => {
    show = true;
    this.setState({a:4})
  }
handleClose = () => {
    show = false;
    this.setState({a:4})
  }


componentDidMount(){  
  this.setState({description:this.props.name})
  if(this.props.haveB === 'true'){
    bHolds = <div>
      
    </div>
  }else{
    bHolds = <div>
      <Button 
        variant="primary"
        onClick={
          this.handleShow                 
        }
        className='ma1 reds tc w-20 h-20'
        >
        Pick a side and take bet
      </Button>
    </div>
  }
  this.setState({b:299});
}


modalMade = () => {
  mods = <div>
  <MyVerticallyCenteredModal
                  show={this.state.modalShow}
                  onHide={this.modalClose}
                  betid = {this.props.betid}
                  description = {this.props.name}
                  total = {this.props.price}
                  expiry = {this.props.expiry}
                  email = {this.props.email}
                  userid = {this.props.userId}
                  bettype = 'pile'
                  betfor = {this.props.currentFor}
                  betagainst = {this.props.currentAgainst}
                  mystate = {this.state}
                  closemo = {this.modalScrew}
                /> 
                </div>;

  this.setState({betid: this.props.betid});
  this.setState({ modalShow: true });
  this.setState({a: 145});
}

  changeRoute = (route) => {
    this.props.changeRoute(route);
  }

  modacall = () => {
    console.log()
    //this.modalMade();
    setTimeout(function () {
    this.modalMade();

    }.bind(this), 250);
    this.modalMade();
  }

  render(){
    
  return (
    

    <div >


    

      <div className='bl br tc red' href="">
        <Container>
          <Row>
            <Col md className='f4 tc bt bb ma1'>{this.props.name}</Col>
              <Col xs={2} className='bb bt ma1'>{this.props.currentFor}</Col>
              <Col xs={2} className='bb bt ma1'>{this.props.currentAgainst}</Col>            
              <Col xs={2} className='bb bt ma1'>{ this.props.price }</Col>        
              <Col xs={2} className='bb bt ma1'>{this.props.expiry}</Col>
          </Row>
          <hr/>
          <Row className='tc'>
            <Col className='xl'>
            
                {bHolds}

                
              

            </Col>
          </Row>
        </Container>
        {mods}
      </div>
      <br/>

      <Modal show={show} onHide={this.handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>{this.state.description}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={this.handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={this.handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

    </div>
  );
}
}
