import React, { Component } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import Particles from "react-particles-js";
import SignInHome from "./components/SignInHome";
import Profile from "./components/Profile";
import MyBets from "./components/MyBets";
import CreateBet from "./components/CreateBet";
import SignIn from "./components/signIn";
import Home from "./components/Home";
import HowThisWorks from "./components/HowThisWorks";
import Fees from "./components/Fees";
import CreateAccount from "./components/CreateAccount";
import Buy from "./components/Buy";
//import MoreInfo from './components/MoreInfo';

//import BuyConfirm from "./components/BuyConfirm";
 
let cur; 
let runOnce = false;
let waiter = false;

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
    a : 1,
    email: '',
    userId: '000',  
    route: 'home',
  };
  }

  backgroundOptions = {
    particles: {
      number: {
        value: 200,
        density: {
          enable: true,
          value_area: 800
        }
      }
    }
  };

loadUser = (email, ider) => {
   this.setState({email: email});
   console.log("the given id  " + ider + " and the emai is " + email);
   if(id){
    this.setState({userId: ider});
    console.log("run");
   }else{
    console.log('i really tun');
      this.getId(email);
   }
   
 }  


unloadUser = () => {
  this.setState({email: ''});
}

reload = (num) => {
  this.setState({state: this.state});
}



changeRoute = (newer) =>{
  this.setState({'route' : newer});
  console.log("New route " +  newer);
}

getId =  async (email) => {
    fetch('http://localhost:3000/getId', {
     method: 'post',
     headers: {'Content-Type': 'application/json'},
     body: JSON.stringify({
        email: email
     })
    })

    .then(response => response.json())
    .then(data => {
      console.log(data + " is users Id! Finnaly");
      waiter = true; 
      this.setState({userId: data});
      
     // console.log("The id is " + this.state.userId);
       
      })
}

  render() {
    let { route } = this.state;

    if(route === 'home'){
         cur =  <Home changeRoute={this.changeRoute} loadUser={this.loadUser}/>
    }
    if(route === 'Reg'){
         cur =  <CreateAccount 
         changeRoute={this.changeRoute} 
         loadUser={this.loadUser}
         />
    }
  

    if(route === 'Fees'){
             cur =  <Fees changeRoute={this.changeRoute}/>
    }

    if(route === 'weWork'){
        cur =  <HowThisWorks changeRoute={this.changeRoute}/>
    }
    if(this.state.route === 'Explore' && waiter === true){
        cur =  <SignInHome changeRoute={this.changeRoute} unloadUser={this.unloadUser}
          email={this.state.email} reload={this.reload} userId={this.state.userId}/>
    }


    if(route === 'Create'){
         cur =  <CreateBet changeRoute={this.changeRoute} unloadUser={this.unloadUser} 
         userid={this.state.userId} email={this.state.email}/>
    }


    if(route === 'myBets'){
         cur =  <MyBets changeRoute={this.changeRoute} unloadUser={this.unloadUser} email={this.state.email}/>

    }
   
    if(route === 'Profile'){
         cur =  <Profile changeRoute={this.changeRoute} unloadUser={this.unloadUser}/>
    }
    
    if(route === 'SignIn'){ 
         cur =  <SignIn 
         changeRoute={this.changeRoute}
             loadUser={this.loadUser}/>
    }

    if(route === "buy"){
      cur = <Buy changeRoute={this.changeRoute} unloadUser={this.unloadUser}  
      email={this.state.email}/>;
    }

    return (
      <div>
        <Particles className="particles" params={this.backgroundOptions} />
        {cur}
      </div>
    );
  }
}

export default App;
