import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import {Modal, Button} from "react-bootstrap";

class CreateConfirmModal extends Component {
  constructor(props) {
    super();
    this.state = {
      justConfirm: true
    }
  }



  buy = () =>{
     fetch('http://localhost:3000/createBet', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body:JSON.stringify({
              description: this.props.betdiscription,
              amountfor: this.props.price
          })
        })
   .then(response => response.json())
   .then(user => {
    if(user.id){   
      console.log(user.id);
   //   this.changeRoute("Explore");

    }
   })
  }

    
  render() {
    let currentDisplay = 
    <div>
    <Modal
        {...this.props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          
        </Modal.Header>
        <Modal.Body className='tc ma3'>
          <h4 className='tc'>Are you sure you want to create and take the bet: </h4>
          <p>
            Are you sure you want to bet {this.props.betdiscription}.
            You will bet B{this.props.price}, at the odds of {this.props.odds}.
            This bet will expire on {this.props.expiry} and the default winner will be {this.props.defualtwinner}.
            
            </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={this.props.onHide}>Cancel</Button>
          <Button onClick={this.buy}>Buy!</Button>
        </Modal.Footer>
      </Modal>
      </div>
    return ( 
      <div>
      {currentDisplay}
      </div>
    )
  }
}

export default CreateConfirmModal;
