            <Col className='xl'>
              <button className='ma1 blues'>More Info</button>  


              <Popup trigger={<button className='ma1 reds'> Take Bet</button>} position="center">
                <div>
                  

                  
                </div>
              </Popup>
            </Col>
          </Row>
        </Container>

      </div>
      <br/>
    </div>
  );
}
}
