import React, { Component } from "react";
import {Container, Row, Col, Button, InputGroup, FormControl,Form } from "react-bootstrap";
import NavbarIn from "../navbar/NavbarIn";
import IndividualGroup from './IndividualGroup';

let name = "";
let subject = "";
let potCode = "";
let joinError;
let joinSucces = "woo"; 
let Holder; 
let createError = "";
let createSuccess = "";
let myGroupArray = []; 
let rightSide = <div> </div>;

class Groups extends Component {
  constructor(props) {
    super(props);
    this.state = {
      a:1,
      b:3
    }
  }

  unloadUser = () => {
    this.props.unloadUser();
  }

  changeRoute = (name) => {
    this.props.changeRoute(name);
  }

  createGroup = () =>{
    //<CreateGroupModal/>
  }

  render() {
    return (
      <div>
        <NavbarIn changeRoute={this.changeRoute} unloadUser={this.unloadUser} userId={this.props.userId}/>
        <br/><br/><br/>
        <h1 className="tc white i f1 font">My Groups</h1>
        <br/><br/>
        <Container>
          <Row className="justify-content-between centre f4 solidBR bg-white">
            <Col>
            <h3>Create a Group</h3>
            <hr/>
            <p>Want to take private bets with just you and your friends? Create a group. In a group, 
              you can create bets that are just between you and your friends. It's the perfect way to
              create fun banter between you and your group. Just click the button below to get started
            </p>
            <Button variant="outline-secondary" size='lg' block onClick={this.createGroup}>Create Group!</Button>
            </Col>
            <Col>
            <h3>Join a Group</h3>
            <hr/>
            <p>If your friends are already in a group together and you want to ne included, ask any of them
              for the group code. Once you've got that, just enter it into the field below to join the group!
              <p>Please note, it is case sensitive</p>
            </p>
            <br/>
            <InputGroup className="mb-3 sm  ">
            <FormControl onChange={this.handleChangeBet} aria-label="Default" aria-describedby="inputGroup-sizing-default"/>
         </InputGroup>
          </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Groups;
