      </InputGroup>
      Will Happen before
      <InputGroup className="mb-3 sm">
        <FormControl aria-label="Default" aria-describedby="inputGroup-sizing-default"/>
      </InputGroup>
      </h3>
    </div>;
    
    let current;
    if(this.state.Choice === 1){
      current = choiceOne;
