import React, { Component } from "react";
import NavbarIn from "./navbar/NavbarIn";
import {Container, Row, Col, ButtonToolbar, ToggleButton, ToggleButtonGroup} from "react-bootstrap";

class Buy extends Component{
    constructor(props) {
        super(); 
        this.state = {

        };
      }

      changeRoute = (name) =>{
        this.props.changeRoute(name); 
      }

      unloadUser = () => {
        this.props.unloadUser();
      } 

      render(){
          return(
              <div>
                  <NavbarIn changeRoute={this.changeRoute} unloadUser={this.unloadUser}/>
                  <br/><br/><br/>
                  <Container>
                      <Row>
                          <Col className="tc f3 solidBR">
                              How many B's would you like to buy?
                              <ToggleButtonGroup
                  className="center "
                  type="radio"
                  name="options"
                  defaultValue={1}
                ></ToggleButtonGroup>
                <ToggleButton className="ma3" value = {1}>100B for R50</ToggleButton>
                          </Col>
                      </Row>
                  </Container>
              </div>
          )
      }
}

export default Buy;
