getGroups = (id) => {
  fetch('http://localhost:3000/getGroups', {
       method: 'post', 
       headers: {'Content-Type': 'application/json'},
       body: JSON.stringify({ 
        id: id 
       })
      })    
      .then(response => response.json())
      .then(data => {

        myGroupArray = data.map((user, i) => { 
          return <IndividualGroup key={i} name={data[i].name} amount={data[i].size} 
                  refresh={this.refresh} code={data[i].id}
                  />
        })

    })
         this.setState({a:this.state.a +1});
    }

createId = () => {
    let id = "";
    for(let a = 0; a < 5; a++){
      id += this.generate();
    }
    fetch('http://localhost:3000/checkId', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body:JSON.stringify({
              id: id,
              table: 'groups'
          })
        })
         .then(response => response.json())
         .then(user => {
            if(user !== 'Good'){
                this.redo();
            }else{
              this.commit(id);
            }
         })

  }

  tryJoin = () => {
    if(potCode !== ""){
      if(potCode.length === 5){
        fetch('http://localhost:3000/checkId', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body:JSON.stringify({
              id: potCode,
              table: 'groups'
          })
        }) 
        .then(response => response.json())
        .then(user => {
          if(user === 'Good'){
            joinError = "Incorrect code";            
            this.setState({a:this.state.a +1});
          }else{
            this.join();
          }
       })
    }else{
      joinError = "Incorrect code";    
       this.setState({a:this.state.a +1});
      }
    }
     this.setState({a:this.state.a +1})
  }

 

  generate = () => {
   const first = Math.round(Math.random() * 64);
   let a;
   if(first < 10){
      a = String.fromCharCode(first + 48);
   }
   if(first > 9 && first < 38){
      a = String.fromCharCode(first + 53);
   }
   if(first > 37 && first < 64){
      a = String.fromCharCode(first + 59);
   }
   return a;
  }

  redo = () => {
  this.createId();
}


