import React, { Component } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import NavbarIn from "../navbar/NavbarIn";
import IndividualGroup from './IndividualGroup';

let name = "";
let subject = "";
let potCode = "";
let joinError;
let joinSucces = "woo"; 
let Holder; 
let createError = "";
let createSuccess = "";
let myGroupArray = []; 
let rightSide = <div>

</div>;
class Groups extends Component {
  constructor(props) {
    super(props);
    this.state = {
      a:1,
      b:3
    }
  }

  changeRoute = (name) => { 
    this.props.changeRoute(name); 
    
  }

  unloadUser = () => {
  this.props.unloadUser();
}
create = () => {
  if(name !== "" && subject !== ""){
    if(name.length >20){//Check this!!
      createError = "Please enter a shorter name";
      createSuccess = "";
    }
    else{
      if(subject.length > 49){
        createError = 'Please enter a shorter subject';
        createSuccess = "";
      }else{
        this.createId();
        createError = "";    
      }

    }

  }else{
    createError = "You must fill all fields.";
    createSuccess = "";

  }
  this.setState({a:11});
}
commit = (id) => {
  fetch('http://localhost:3000/makeGroup', {
             method: 'post',
             headers: {'Content-Type': 'application/json'},
             body:JSON.stringify({
               id: id,
               name: name,
               subject: subject,
               email: 'j@gmail.com'
           })
        })
         .then(response => response.json())
         .then(user => {
          if(user === "Success"){
            
            window.scrollTo(0, 0); 
            createSuccess = "Success";
            this.stateSetter();
       }
       if(user === "Too many"){
        createError = "You are a part of the maximum amount of groups. Consider leaving one.";
       }
          console.log(user);
         
         })

       }

stateSetter = () => {
  console.log("I guess i ran");
  this.setState({a:129});
  //this.setState({toSet:setTo});
  if(this.state.a === '1'){
    this.setState({a:25});
  }else{
    this.setState({a: 1});
  }
}


handleChange = (newer) => {
  name = newer.target.value.toLowerCase();
}
handleChangeSubject = (newer) => {
  subject = newer.target.value.toLowerCase();
}
handleChangeCode = (newer) => {
  potCode = newer.target.value;
}
componentDidMount(){  
  this.getGroups();
  this.setRight();
}

 join = () => {

    fetch('http://localhost:3000/joinGroup', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body:JSON.stringify({
              userid: 'Ht?tq',
              code:potCode,
              email: 'j@gmail.com'
          })
        })
         .then(response => response.json())
         .then(user => {
              if(user === 'Already in'){
                console.log("Is it run");
