import React, { Component } from "react";
import { Col, Row, Container, Button} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import "./bets.css";
import MyVerticallyCenteredModal from './MyVerticallyCenteredModal';


export default class NavBar extends Component{
constructor(props) {
    super();
    this.state = {
      id: 111111, 
      Price: 0,
      Odds: "",
      Expiry:"",
      BetDiscription:"XXX",
      modalShow: false,

    };
  }



  changeRoute = (route) => {
    this.props.changeRoute(route);
  }

  lose = () =>{
    this.choose("lose");
  }

  choose = (outcome) =>{
    let side = "";
    let winner = "";
    if(this.props.userId == this.props.creator){
      side = "creator";
      if(outcome == 'lose'){
        winner = 'be';
      }else{
        winner = 'cr';
      }
    }else{
      side = "better";
      if(outcome == 'lose'){
        winner = 'cr';
      }else{
        winner = 'be';
      }
    }
    console.log("I am " + side + " - winner is " + winner + ". Bet id is " + this.props.betid);
    fetch('http://localhost:3000/chooseMatchWin', {
    method: 'post',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
      userId: this.props.userId,
      creator: this.props.creator,
      better: this.props.better,
      amount: this.props.amount,
      betid: this.props.betid,
      side:side,
      sideWon:winner

    })
   })
   .then(response => response.json())
   .then(data => {
    console.log(data)
    if(data.length > 0){
      console.log("has");
    }else{
      console.log("I am the first side to submit");
      this.firstSide();
    }
    

  })
 }

firstSide = () =>{
    fetch('http://localhost:3000/chooseMatchWinp2', {
    method: 'post',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
      userId: this.props.userId,
      creator: this.props.creator,
      better: this.props.better,
      amount: this.props.amount,
      betid: this.props.betid,
      side:side,
      sideWon:winner

    })
   })
   .then(response => response.json())
   .then(data => {
    console.log(data)
    if(data.length > 0){
      console.log("has");
    }else{
      console.log("I am the first side to submit");
      this.firstSide();
    }
    

  })


}


  render(){
    let modalClose = () => {
      this.setState({ modalShow: false });
        
    }
  return (
    

    <div >


    

      <div className='bl br tc' href="">
        <Container>
          <Row>
            <Col md className='f4 tc bt bb ma1'>{this.props.name}</Col>
            <Col xs={2} className='bb bt ma1'>-</Col>
            <Col xs={2} className='bb bt ma1'>{ this.props.price }</Col>
            
            <Col xs={2} className='bb bt ma1'>{this.props.expiry}</Col>
          </Row>
          <hr/>
          <Row className='tc'>
            <Col className='xl'>
            
                <Button 
                  variant="primary"
                  onClick={this.lose}
                  className='ma1 reds tc w-20 h-20'
                >
                  Not Me
                </Button>
                Who Won?
                <Button 
                  variant="primary"
                  onClick={() => this.setState({ modalShow: true })}
                  className='ma1 reds tc w-20 h-20'
                >
                  Me
                </Button>
                 
              

            </Col>
          </Row>
        </Container>

      </div>
      <br/>
    </div>
  );
}
}
