import React, { Component } from "react";
import { Col, Row, Container, Button} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";


export default class IndividualGroup extends Component{
constructor(props) {
    super();
    this.state = {
    };
  }


  refresh = () => {
    this.props.refresh(this.props.code);
  }

  render(){ 
  return (   
    <div >
      <div className='bl br tc green'>
        <Container>  
          <Row>
            <Col md className='f4 tc bt bb ma1'>{this.props.name}</Col>
            <Col xs={3} className='bb bt ma1'>{this.props.amount}</Col>
          </Row>
          <Row className='tc'>
            <Col className='xl'>
                <Button 
                  variant="primary"
                  onClick={this.refresh}
                  className='ma1 reds tc w-20 h-20'
                >
                  Group Info
                </Button>            
            </Col>
          </Row>
        </Container>
      </div>

      <div className='bl br tc red' href="">
        <Container>
          <Row>
            <Col md className='f4 tc bt bb ma1'>{this.props.name}</Col>
              <Col xs={2} className='bb bt ma1'>{this.props.currentFor}</Col>
              <Col xs={2} className='bb bt ma1'>{this.props.currentAgainst}</Col>            
             {// <Col xs={2} className='bb bt ma1'>{ this.props.price }</Col>        
              }<Col xs={2} className='bb bt ma1'>{this.props.expiry}</Col>
          </Row>
          <hr/>
          <Row className='tc'>
            <Col className='xl'>
            
          <Button 
                  variant="primary"
                  onClick={
                    this.meWon
                   
                  }
                  className='ma1 reds tc w-20 h-20'
                >
                  Me!
                </Button>
                Who Won?
              <Button 
                  variant="primary"
                  onClick={
                    this.notMe
                   
                  }
                  className='ma1 reds tc w-20 h-20'
                >
                  Not Me!
              </Button>

                
              

            </Col>
          </Row>
        </Container>
        {mods}
      </div>
      <br/>
    </div>
  );
}
}
