import React, { Component } from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import NavbarIn from './navbar/NavbarIn'

class CreateBet extends Component {
  constructor(props) {
    super();
    this.state = { 
      Choice:1
    

  };
  
}

  render() {
    let choiceOne = <div>a</div>;
    let choiceTwo = <div>b</div>;
    let choiceThree = <div>c</div>;
    
    let current;
    if(this.state.Choice === 1){
      current = choiceOne;
    }
    if(this.state.Choice === 2){
      current = choiceTwo;
    }
    if(this.state.Choice === 3){ 
      current = choiceThree;
    }

    return (
     <div>
     <NavbarIn/>
     <br/><br/><br/>
     <h1 className="tc white i f1 font">Create a bet!</h1>
     <br/><br/>
        <Container>
  			<Row>
 			   <Col className='f4 solidBR bg-white'>.
         <br/>
          <Container className='tc'>
            <Row>
              <Col>We Offer three main types of bets. With proper wording, almost any bet should fit.
              <br/>Ps. If you need, there is a proper discription at the bottom.
              <br/>

              I bet That ______ will happen before [Date] will happen<br/>
              I bet That ______ will ______ <br/>
              I bet that ______ is really actualy ______ <br/>
              I bet that ______ happened ________ <br/>
              <hr/>


              </Col>
            </Row>
            <Row>
              <Col>
             
                  <Button variant="outline-primary" onClick={() => this.setState({Choice: 1})}>
                    I bet that ______ will happen before ______.
                  </Button>
               
              </Col>
              <Col>
                
                  <Button variant="outline-primary" onClick={() => this.setState({Choice: 2})}>
                    I bet that ______ will ______.
                  </Button>
          
              </Col>
              <Col>
                  <Button variant="outline-primary" onClick={() => this.setState({Choice: 3})}>
                    I bet that ______ will happen before ______.
                  </Button>
              </Col>
            </Row>
            <hr/>
            <h1> {current}</h1>
            <br/>

            <hr/>
            <br/>
            <Row>
              <Col>Okay, heres the deal... 
              <br/>
              We only use three bet types to make the whole betting and taking bets simpler.
              The first type is that: I bet that ______ will happen before.   
              </Col>
            </Row>
          </Container>
          
          <br/>
          

 			   </Col>
  		
			</Row>
		</Container>
    <br/>
      </div>
    );
  }
}

export default CreateBet;
