import React, { Component } from "react";
import { Col, Row, Container, Button} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";


export default class IndividualGroup extends Component{
constructor(props) {
    super();
    this.state = {
    };
  }



  changeRoute = (route) => {
    this.props.changeRoute(route);
  }


  render(){
    let modalClose = () => {
      this.setState({ modalShow: false });
      this.props.bought();
    }
  return (
    

    <div >


    

      <div className='bl br tc' href="">
        <Container>
          <Row>
            <Col md className='f4 tc bt bb ma1'>{this.props.name}</Col>
            <Col xs={2} className='bb bt ma1'>{ (Math.round(parseFloat(this.props.Odds) * 10000) /10000) + ":1" }</Col>
            <Col xs={2} className='bb bt ma1'>B{this.props.amount}</Col>
            <Col xs={2} className='bb bt ma1'>{this.props.expiry}</Col>
          </Row>
          <hr/>
          <Row className='tc'>
            <Col className='xl'>
            
                <Button 
                  variant="primary"
                  onClick={() => this.setState({ modalShow: true })}
                  className='ma1 reds tc w-20 h-20'
                >
                  Take Bet!
                </Button>

              
              

            </Col>
          </Row>
        </Container>

      </div>
      <br/>
    </div>
  );
}
}
