import React, { Component } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import NavbarIn from "../navbar/NavbarIn";
import IndividualGroup from './IndividualGroup';

let name = "";
let subject = "";
let potCode = "";
let joinError;
let joinSucces; 
let Holder; 
class Groups extends Component {
  constructor(props) {
    super(props);
    this.state = {
      a:1
    }
  }

  changeRoute = (name) => { 
    this.props.changeRoute(name); 
    
  }

  unloadUser = () => {
  this.props.unloadUser();
}
create = () => {
  this.createId();
}
commit = (id) => {
  fetch('http://localhost:3000/makeGroup', {
             method: 'post',
             headers: {'Content-Type': 'application/json'},
             body:JSON.stringify({
               id: id,
               name: name,
               subject: subject
           })
        })
         .then(response => response.json())
         .then(user => {
          if(user === "Success"){
       
          }
         })
       }

componentDidMount(){  
  this.getGroups();
}

getGroups = () => {
  fetch('http://localhost:3000/getGroups', {
       method: 'post', 
       headers: {'Content-Type': 'application/json'},
       body: JSON.stringify({
        email:'j@gmail.com'
       })
      })   
      .then(response => response.json())
      .then(data => {
        console.log(data);
        let better = "";
        for(let i = 0; i < data.length; i ++){
          if(data[i] !== ','){
            better += data[i]; 
          }
        }
        let array = [];
        let times = (better.length / 5);
        for(let i = 0; i < times; i ++){
          array.push(better.substring(i * 5, (i * 5 ) + 5));
        }
    
          Holder = array.map((user, i) => { 
             return <IndividualGroup key={i} name='jo' amount={i}//name={data[i].description} amount={data[i].total}
                     />


           })
        if(this.state.a === '1'){
        this.setState({a:'2'});
       }else{
        this.setState({a: '1'});
       }
      });

}

handleChange = (newer) => {
  name = newer.target.value;
}
handleChangeSubject = (newer) => {
  subject = newer.target.value;
}
handleChangeCode = (newer) => {
  potCode = newer.target.value;
}

createId = () => {
    let id = "";
    for(let a = 0; a < 5; a++){
      id += this.generate();
    }
 
    fetch('http://localhost:3000/checkId', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body:JSON.stringify({
              id: id,
              table: 'groups'
          })
        })
         .then(response => response.json())
         .then(user => {
            if(user !== 'Good'){
                this.redo();
            }else{
              this.commit(id);
            }
         })

  }

  tryJoin = () => {
    if(potCode !== ""){
      if(potCode.length === 5){
        fetch('http://localhost:3000/checkId', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body:JSON.stringify({
              id: potCode,
              table: 'groups'
          })
        }) 
         .then(response => response.json())
         .then(user => {
            if(user === 'Good'){
                joinError = "Incorrect code"; 
                joinSucces = "";
                this.setState({a:23});
            }else{
              this.join();
            }
         })

      }
      else{
        joinError = "Incorrect code";
        joinSucces = '';
         this.setState({a:24});
      }
    }
  }

  join = () => {
    fetch('http://localhost:3000/joinGroup', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body:JSON.stringify({
              userid: 'Ht?tq',
              code:potCode
          })
        })
         .then(response => response.json())
         .then(user => {
              if(user === 'Already in'){
                console.log("Is it run");
                joinError = "You have already joined this group.";
                joinSucces = '';
              }
              if(user === 'Success'){
                joinSucces = "Success !";
                joinError = "";
              }
         })
         this.setState({a:5});
  }

  generate = () => {
   const first = Math.round(Math.random() * 64);
   let a;
   if(first < 10){
      a = String.fromCharCode(first + 48);
   }
   if(first > 9 && first < 38){
      a = String.fromCharCode(first + 53);
   }
   if(first > 37 && first < 64){
      a = String.fromCharCode(first + 59);
   }
   return a;
  }

  redo = () => {
  this.createId();
}


  render() {
    return (
      <div>
        
        <NavbarIn changeRoute={this.changeRoute} unloadUser={this.unloadUser}/>
         <br />
        <br />
        <h1 className="tc i f1 font white">My Groups</h1>
        <br />
        

        <Container className='tc f3 solidBR'>
        <br/>

          <Row>

          <Col className='pa3 ma3 ba'>
          <br/>
            <h3 className='i pointer'>My Groups</h3>
            <hr/>
            <br/>
            <Container>
              <Row>
                <Col md>
                  Name 
                </Col>
                <Col xs={3}>
                  users
                </Col>
              </Row>
            </Container> 
            {Holder}
            <IndividualGroup name='jojo'/>
          </Col>       

            <Col className='pa3 ma3 ba'>
            <br/>
            <h3 className='i pointer'>Join a group</h3>
            <hr/>
            <br/>
              <span className = 'f4'>
              Join a group by simply entering the groups code.
              <br/>
               <Form>
            <Form.Row>
              <Col>
                Group Code: <b className='red f3'></b>
              </Col>
              <Col className=''>
                <Form.Control onChange={this.handleChangeCode} placeholder="XXXXXXX" />
              </Col>
            </Form.Row>

          </Form>
              </span>
              <br/>
           <Button className='pa5' variant="outline-secondary" size='lg' block onClick={this.tryJoin}>Join Group</Button>
              <br/>
              <span className='red i f4'> {joinError} </span>
              <span className='green i f4'> {joinSucces} </span>
              <hr/>
              <span className='f4'>
              What is a group?
              If you do not want a bet to be open to every user, you can create a bet that 
              is only avaliable to a group. You can control who is in each group by only giving 
              the group code to people you want in the group. 
              </span>
          </Col>
          </Row>
              
       
      
        <Row>
          
          <Col className='pa3 ma3 ba'>
            <h3 className='i pointer'>Create a group</h3>
            <br/>
            <hr/>
          <Container>
            <Row> 
              <Col className='pa3'>
                <Form>
            <Form.Row>
              <Col>
                Group name: <b className='red f3'></b>
              </Col>
              <Col className=''>
                <Form.Control onChange={this.handleChange} placeholder="The A Team" />
              </Col>
            </Form.Row>

            </Form>
            <br/>



              </Col>
              <Col className='pa3'>
                <Form>
            <Form.Row>
              <Col>
                Subject: <b className='red f3'></b>
              </Col>
              <Col className=''>
                <Form.Control onChange={this.handleChangeSubject} placeholder="Optional" />
              </Col>
            </Form.Row> 

          </Form>
              </Col>
 
                <Button variant="outline-secondary" size='lg' block onClick={this.create}>Create Group!</Button>

            </Row>
          </Container>
          <br/>
            
              <span className='f6 black bt bb pa1' onClick={this.terms}>Our Terms of Service and stuff</span>   
          </Col>    

          </Row> 
          <br/>
        
        </Container>
        <br/>
        <br/>
         
        </div>
    );
  }
}

export default Groups;
