import React, { Component } from "react";
import { Col, Row, Container, Button} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import "./bets.css";
import MyVerticallyCenteredModal from './MyVerticallyCenteredModal';

let mods;

export default class ExpiredPileBet extends Component{
constructor(props) {
    super();
    this.state = {

    };
  }

meWon = () => {
  this.submit(this.props.side);
}
submit = (winner) => {
     fetch('http://localhost:3000/getPileBets', {
    method: 'post',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
      userId: this.props.userId,
      id: this.props.betid,
      vote: winner
    })
   })
   .then(response => response.json())
   .then(data => {


   })
}

  changeRoute = (route) => {
    this.props.changeRoute(route);
  }


  render(){
    
  return (
    

    <div >

      <div className='bl br tc' href="">
        <Container>
          <Row>
            <Col md className='f4 tc bt bb ma1'>{this.props.name}</Col>
              <Col xs={2} className='bb bt ma1'>{this.props.currentFor}</Col>
              <Col xs={2} className='bb bt ma1'>{this.props.currentAgainst}</Col>            
             {// <Col xs={2} className='bb bt ma1'>{ this.props.price }</Col>        
              }<Col xs={2} className='bb bt ma1'>{this.props.expiry}</Col>
          </Row>
          <hr/>
          <Row className='tc'>
            <Col className='xl'>
            
          <Button 
                  variant="primary"
                  onClick={
                    this.meWon
                   
                  }
                  className='ma1 reds tc w-20 h-20'
                >
                  Me!
                </Button>
                Who Won?
              <Button 
                  variant="primary"
                  onClick={
                    this.modacall
                   
                  }
                  className='ma1 reds tc w-20 h-20'
                >
                  Not Me!
              </Button>

                
              

            </Col>
          </Row>
        </Container>
        {mods}
      </div>
      <br/>
    </div>
  );
}
}
