import React, { Component } from "react";
import {Container, Row, Col, ButtonToolbar, ToggleButton, ToggleButtonGroup} from "react-bootstrap";
import NavbarIn from "./navbar/NavbarIn";
import IndividaulBet from "./IndividaulBet/IndividaulBet";

class BuyConfirm extends Component {
  constructor(props) {
    super();
    this.state = {
      Price: 0,
      Odds: "",
      Expiry:"",
      BetDiscription:"XXX"

    };
  }

  render() { 
    return (
      <div>
        <NavbarIn />
        <br />
        <br />
        <br />
        <h1 className="tc white i f1 font">Are you sure?</h1>
        <br />
        <br />
        <Container>
          <Row>
            <Col className="tc f3 solidBR">
             
             Just Making Sure...
              <br />
              <hr />
        
              <div className='tc'>
                <Container>
                  <Row>
                    <Col className='f4 tc bl ma1'>
                      `You are about to take the bet that, {this.state.BetDiscription}.
                      You are betting with {this.state.Odds}.
                      You are betting B{this.state.Price}.
                      The bet will expire if terms or not already reached by {this.state.Expiry}
                      Buy clicking "Buy" you are accepting our <a href="">terms of service</a>. 
                      `


                      <br/>
                      <br/>
                      <button></button>
                    </Col>
                  </Row>   
                </Container>
              </div>
              <hr/>
             </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default BuyConfirm;
