        <option>21</option><option>22</option><option>23</option><option>24</option><option>25</option>
        <option>26</option><option>27</option><option>28</option><option>29</option><option>26</option>
        <option>31</option>
      </Form.Control>
    </Form.Group>

    <Form.Group as={Col} controlId="formGridState" placeholder="a" >
      <Form.Label>MM</Form.Label>
      <Form.Control as="select" onChange={this.handleChangeMonth} value="yay">
        <option>Choose...</option>
        <option>1</option><option>2</option><option>3</option><option>4</option><option>5</option>
        <option>6</option><option>7</option><option>8</option><option>9</option><option>10</option>
        <option>11</option><option>12</option>
      </Form.Control>
    </Form.Group>

    <Form.Group as={Col} controlId="formGridState">
      <Form.Label>YYYY</Form.Label>
      <Form.Control as="select" onChange={this.handleChangeYear}>
        <option>Choose...</option>
        <option>...</option>
      </Form.Control>
    </Form.Group>
    aaaaaaaaaaaaaaaaaa
  </Form.Row>

</Form>  
    </h3>
    </div>;


    let choiceTwo = 
    <div>
      <h3 className='tl'> I bet that 
     <InputGroup className="mb-3 sm">
        <FormControl aria-label="Default" aria-describedby="inputGroup-sizing-default"/>
      </InputGroup>
      Will
      <InputGroup className="mb-3 sm">
        <FormControl aria-label="Default" aria-describedby="inputGroup-sizing-default"/>
      </InputGroup>
      </h3>
    </div>;


    let choiceThree = 
    <div>
      <h3 className='tl'> I bet that 
     <InputGroup className="mb-3 sm">
        <FormControl aria-label="Default" aria-describedby="inputGroup-sizing-default"/>
      </InputGroup>
    
      </h3>
    </div>;
    
    let current;
    if(this.state.Choice === 1){
      current = choiceOne;
    }
    if(this.state.Choice === 2){
      current = choiceTwo;
    }
    if(this.state.Choice === 3){ 
      current = choiceThree;
    }

