import React, { Component } from "react";
import { Col, Row, Container, Button} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import "./bets.css";
import MyVerticallyCenteredModal from './MyVerticallyCenteredModal';


export default class NavBar extends Component{
constructor(props) {
    super();
    this.state = {
      id: 111111, 
      Price: 0,
      Odds: "",
      Expiry:"",
      BetDiscription:"XXX",
      modalShow: false,

    };
  }



  changeRoute = (route) => {
    this.props.changeRoute(route);
  }

  lose = () =>{
    fetch('http://localhost:3000/choseMatchWin', {
    method: 'post',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
      userId: this.props.userId,
      database: database
    })
   })
   .then(response => response.json())
   .then(data => {
    console.log(data)
    console.log("phase 1");
    for (var i = 0; i < data.length; i++) {
      if(data[i].better){
        if(data[i].better === this.props.userId){
          data[i].side = "for";
          console.log("phase 2");
        }else{
          data[i].side = "against";
          console.log("phase 3");
        } 
      }
    }

  })
 }


  render(){
    let modalClose = () => {
      this.setState({ modalShow: false });
        
    }
  return (
    

    <div >


    

      <div className='bl br tc' href="">
        <Container>
          <Row>
            <Col md className='f4 tc bt bb ma1'>{this.props.name}</Col>
            <Col xs={2} className='bb bt ma1'>-</Col>
            <Col xs={2} className='bb bt ma1'>{ this.props.price }</Col>
            
            <Col xs={2} className='bb bt ma1'>{this.props.expiry}</Col>
          </Row>
          <hr/>
          <Row className='tc'>
            <Col className='xl'>
            
                <Button 
                  variant="primary"
                  onClick={ this.lose}
                  className='ma1 reds tc w-20 h-20'
                >
                  Not Me
                </Button>
                Who Won?
                <Button 
                  variant="primary"
                  onClick={() => this.setState({ modalShow: true })}
                  className='ma1 reds tc w-20 h-20'
                >
                  Me
                </Button>
                 
              

            </Col>
          </Row>
        </Container>

      </div>
      <br/>
    </div>
  );
}
}
